import React, { Fragment } from "react";
import mealsImage from "../../assets/meals.jpg";
import classes from "./Header.module.css";
import HeaderCartButton from "./HeaderCartButton";
import { Link } from "react-router-dom";

const Header = (props) => {
  return (
    <Fragment>
      <header className={classes.header}>
        <h1>ReactMeals</h1>
        <div className={classes.box}>
        <select name="pages" id="pages" >
          <option value="allitems"><Link to="/">All Items</Link></option>
          <option value="pizzas"><Link to="/pizzas">Pizzas</Link></option>
          <option value="appetizers"><Link to="/appetizers">Appetizers</Link></option>
          <option value="desserts"><Link to="/desserts">Desserts</Link></option>
        </select>
        </div>
        <HeaderCartButton onClick={props.onShowCart} />
      </header>
      <div className={classes["main-image"]}>
        <img src={mealsImage} alt="A table with food."></img>
      </div>
    </Fragment>
  );
};

export default Header;
